import { Component, OnInit } from '@angular/core';
import { AuthService } from '../authService/auth.service';
import { AlertController } from '@ionic/angular';
import { LoaderService } from 'src/app/loader/loader.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.page.html',
  styleUrls: ['./login.page.scss'],
})
export class LoginPage implements OnInit {
  username: any;
  password: any;

  constructor(
    private authService: AuthService, 
    private alertController: AlertController,
    private ionLoader: LoaderService
    ) {}

  ngOnInit() {}

  public SubmitLoginDetails() {
    this.ionLoader.showLoader();
    const form = {
      username: this.username,
      password: this.password,
    };
    this.authService.login(form).subscribe((res: any) => {
        if (res.success) {
          localStorage.setItem('token', res.token);
          this.ionLoader.hideLoader();
        } else {
          this.presentAlert(res);
          this.ionLoader.hideLoader();
        }
      },(err) => {
        this.ionLoader.hideLoader();
        console.log(err);
      }
    );
  }

  async presentAlert(res: any) {
    const alert = await this.alertController.create({
      header: 'Alert',
      subHeader: 'Ivalid Input',
      message: res.message,
      buttons: ['OK'],
    });

    await alert.present();
  }
}
<ion-header class="ion-no-border">
  <ion-toolbar>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-card class="ion-no-margin">
    <ion-img src="assets/images/header.png"></ion-img>
    <ion-card-header>
      <ion-card-title>
        LOGIN
      </ion-card-title>
      <ion-card-subtitle>
        This is  social media platform for artist - and it's all about visuals.
      </ion-card-subtitle>
    </ion-card-header>

    <ion-card-content>
      <form (ngSubmit)="SubmitLoginDetails()">
        <ion-item>
          <ion-label position="floating" color="primary">Username</ion-label>
          <ion-input type="text" [(ngModel)]="username" name="title"></ion-input>
        </ion-item>

        <ion-item>
          <ion-label position="floating" color="primary">Password</ion-label>
          <ion-input type="password" [(ngModel)]="password" name="title"></ion-input>
        </ion-item>

        <div class="signin-btn">
          <ion-button fill="solid" shape="round" class="loginbtn" type="submit">
            Login
          </ion-button>
        </div>
      </form>

      <div class="text-tag">
        <span>New user <a [routerLink]="['/auth/signup']">SignUp here</a></span>
      </div>

    </ion-card-content>
  </ion-card>
</ion-content>
